    if (row === 9) {
      return true;
    }

    const nextRow = col === 8 ? row + 1 : row;
    const nextCol = col === 8 ? 0 : col + 1;

    // If the cell is already defined, move to the next cell
    if (grid[row][col] !== undefined) {
      return fillCell(nextRow, nextCol);
    }

    // Try each of the shuffled numbers in this cell
    for (let num of nums) {
      if (isValid(grid, row, col, num)) {
        grid[row][col] = num;
        if (fillCell(nextRow, nextCol)) {
          return true;
        }
      }
    }

    // If we've tried all the numbers and none work, reset the cell and backtrack
    grid[row][col] = undefined;
    return false;
  }

  // Define a helper function to check if a number is valid in a given cell
  function isValid(grid, row, col, num) {
